mapper.select:
SELECT *
FROM visa_user_api
WHERE username = #{username} where status='ACTIVE';

mapper.update:
UPDATE visa_user_api SET
visa_id=#{visa_id},"birthday"=#{dateofbirth} ,"sex"=#{sex},"country"=#{country},"address"=#{address},"visa_date"=#{visakigen},"visa_type"=#{visa_type},"note"=#{note}
WHERE username = #{username} AND where status='ACTIVE';
